import React, { useState } from "react";
import axios from "axios";

function CandidateRegister() {
  const [formData, setFormData] = useState({
    name: "",
    school: "",
    department: "",
    yearOfStudy: "",
    position: "",
    manifesto: "",
  });

  const schools = ["School of Computing", "School of Business", "School of Education"];
  const departments = [
    "Computer Science",
    "Information Technology",
    "Accounting",
    "Marketing",
    "Education Management",
  ];
  const years = ["Year 1", "Year 2", "Year 3", "Year 4"];
  const positions = [
    "Guild President",
    "Vice President",
    "Finance Minister",
    "Information Minister",
    "Academic Minister",
  ];

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/candidates/register", formData);
      alert("✅ Candidate registered successfully!");
      setFormData({
        name: "",
        school: "",
        department: "",
        yearOfStudy: "",
        position: "",
        manifesto: "",
      });
    } catch (error) {
      console.error("Error:", error.response ? error.response.data : error.message);
      alert("❌ Failed to register candidate");
    }
  };

  return (
    <div className="p-6 bg-white rounded-2xl shadow-md">
      <h2 className="text-2xl font-bold mb-4 text-blue-700 text-center">
        Candidate Registration
      </h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="name"
          placeholder="Full Name"
          onChange={handleChange}
          value={formData.name}
          className="w-full p-2 border rounded"
          required
        />

        <select
          name="school"
          onChange={handleChange}
          value={formData.school}
          className="w-full p-2 border rounded"
          required
        >
          <option value="">Select School</option>
          {schools.map((school) => (
            <option key={school} value={school}>
              {school}
            </option>
          ))}
        </select>

        <select
          name="department"
          onChange={handleChange}
          value={formData.department}
          className="w-full p-2 border rounded"
          required
        >
          <option value="">Select Department</option>
          {departments.map((dept) => (
            <option key={dept} value={dept}>
              {dept}
            </option>
          ))}
        </select>

        <select
          name="yearOfStudy"
          onChange={handleChange}
          value={formData.yearOfStudy}
          className="w-full p-2 border rounded"
          required
        >
          <option value="">Select Year of Study</option>
          {years.map((yr) => (
            <option key={yr} value={yr}>
              {yr}
            </option>
          ))}
        </select>

        <select
          name="position"
          onChange={handleChange}
          value={formData.position}
          className="w-full p-2 border rounded"
          required
        >
          <option value="">Select Position</option>
          {positions.map((pos) => (
            <option key={pos} value={pos}>
              {pos}
            </option>
          ))}
        </select>

        <textarea
          name="manifesto"
          placeholder="Manifesto / Vision Statement"
          onChange={handleChange}
          value={formData.manifesto}
          className="w-full p-2 border rounded"
        />

        <button
          type="submit"
          className="w-full bg-blue-700 text-white p-2 rounded hover:bg-blue-800 transition"
        >
          Register Candidate
        </button>
      </form>
    </div>
  );
}

export default CandidateRegister;
