import React, { useState, useEffect } from "react";
import axios from "axios";

export default function StudentDashboard() {
  const [candidates, setCandidates] = useState([]);
  const [loading, setLoading] = useState(true);
  const [votedCandidateId, setVotedCandidateId] = useState(null);

  // Replace with actual logged-in student ID
  const studentId = "YOUR_STUDENT_ID_HERE";

  useEffect(() => {
    fetchCandidates();
  }, []);

  const fetchCandidates = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/votes/candidates");
      setCandidates(res.data);
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const castVote = async (candidateId) => {
    try {
      const res = await axios.post("http://localhost:5000/api/votes/vote", {
        studentId,
        candidateId,
      });
      alert(res.data.message);
      setVotedCandidateId(candidateId);
    } catch (error) {
      alert(error.response?.data?.message || "Error casting vote");
      console.error(error);
    }
  };

  if (loading) return <p>Loading candidates...</p>;

  return (
    <div>
      <h1>Student Dashboard</h1>
      <h2>Available Candidates</h2>
      {candidates.length === 0 ? (
        <p>No candidates available.</p>
      ) : (
        <ul>
          {candidates.map((c) => (
            <li key={c._id} style={{ marginBottom: "1rem" }}>
              <strong>{c.name}</strong> - {c.position} (
              {c.department}, Year {c.yearOfStudy})
              <br />
              {votedCandidateId === c._id ? (
                <span style={{ color: "green" }}>You voted for this candidate âœ…</span>
              ) : (
                <button onClick={() => castVote(c._id)}>Vote</button>
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
