import React, { useEffect, useState } from "react";
import axios from "axios";

function AdminDashboard() {
  const [candidates, setCandidates] = useState([]);
  const [formData, setFormData] = useState({
    title: "",
    position: "",
    school: "",
    department: "",
    photo: null,
  });
  const [editingId, setEditingId] = useState(null);

  const fetchCandidates = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/candidates");
      setCandidates(res.data);
    } catch (err) {
      console.error("Failed to fetch candidates:", err);
    }
  };

  useEffect(() => {
    fetchCandidates();
  }, []);

  const handleChange = (e) => {
    if (e.target.name === "photo") {
      setFormData({ ...formData, photo: e.target.files[0] });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    for (let key in formData) data.append(key, formData[key]);

    try {
      if (editingId) {
        await axios.put(
          `http://localhost:5000/api/candidates/update/${editingId}`,
          data
        );
        setEditingId(null);
      } else {
        await axios.post("http://localhost:5000/api/candidates/add", data);
      }
      setFormData({
        title: "",
        position: "",
        school: "",
        department: "",
        photo: null,
      });
      fetchCandidates();
    } catch (err) {
      console.error(err);
    }
  };

  const handleEdit = (candidate) => {
    setFormData(candidate);
    setEditingId(candidate._id);
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:5000/api/candidates/delete/${id}`);
    fetchCandidates();
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-center">Admin Dashboard</h1>

      {/* Form */}
      <div className="bg-white p-6 rounded-lg shadow-md max-w-xl mx-auto mb-8">
        <h2 className="text-xl font-semibold mb-4">
          {editingId ? "Edit Candidate" : "Add Candidate"}
        </h2>
        <form
          onSubmit={handleSubmit}
          className="grid grid-cols-1 gap-4"
        >
          <input
            name="title"
            placeholder="Title"
            value={formData.title}
            onChange={handleChange}
            className="border p-2 rounded"
            required
          />
          <input
            name="position"
            placeholder="Position"
            value={formData.position}
            onChange={handleChange}
            className="border p-2 rounded"
            required
          />
          <input
            name="school"
            placeholder="School"
            value={formData.school}
            onChange={handleChange}
            className="border p-2 rounded"
            required
          />
          <input
            name="department"
            placeholder="Department"
            value={formData.department}
            onChange={handleChange}
            className="border p-2 rounded"
            required
          />
          <input
            type="file"
            name="photo"
            onChange={handleChange}
            className="border p-2 rounded"
          />
          <button
            type="submit"
            className="bg-blue-600 text-white p-2 rounded hover:bg-blue-700 transition"
          >
            {editingId ? "Update" : "Add"} Candidate
          </button>
        </form>
      </div>

      {/* Candidate Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {candidates.map((c) => (
          <div
            key={c._id}
            className="bg-white rounded-lg shadow-md p-4 flex flex-col items-center"
          >
            <img
              src={`http://localhost:5000/uploads/${c.photo}`}
              alt={c.title}
              className="w-32 h-32 object-cover rounded-full mb-4"
            />
            <h3 className="text-lg font-semibold">{c.title}</h3>
            <p className="text-gray-600">{c.position}</p>
            <p className="text-gray-500">{c.school}</p>
            <p className="text-gray-500 mb-4">{c.department}</p>
            <div className="flex gap-2">
              <button
                onClick={() => handleEdit(c)}
                className="bg-yellow-400 text-white px-3 py-1 rounded hover:bg-yellow-500 transition"
              >
                Edit
              </button>
              <button
                onClick={() => handleDelete(c._id)}
                className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600 transition"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default AdminDashboard;
