import React, { useState, useEffect } from "react";
import API from "../api/api";
import { useNavigate } from "react-router-dom";

const AdminDashboard = () => {
  const [candidates, setCandidates] = useState([]);
  const [newCandidate, setNewCandidate] = useState("");
  const navigate = useNavigate();

  // ✅ Get token from localStorage
  const token = localStorage.getItem("token");

  // ✅ Redirect to login if token is missing
  useEffect(() => {
    if (!token) {
      alert("Please log in first");
      navigate("/");
    }
  }, [navigate, token]);

  // ✅ Fetch all candidates (token included in headers)
  const fetchCandidates = async () => {
    try {
      const res = await API.get("/admin/candidates", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setCandidates(res.data);
    } catch (err) {
      if (err.response?.status === 401) {
        alert("Invalid or expired token. Please log in again.");
        localStorage.removeItem("token");
        navigate("/");
      } else {
        alert(err.response?.data?.message || "Failed to fetch candidates");
      }
    }
  };

  useEffect(() => {
    fetchCandidates();
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  // ✅ Add candidate (token in header)
  const handleAddCandidate = async () => {
    if (!newCandidate) return alert("Enter candidate name");
    try {
      await API.post(
        "/admin/candidates",
        { name: newCandidate },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setNewCandidate("");
      fetchCandidates();
    } catch (err) {
      alert(err.response?.data?.message || "Failed to add candidate");
    }
  };

  // ✅ Delete candidate (token in header)
  const handleDeleteCandidate = async (id) => {
    if (!window.confirm("Are you sure you want to delete this candidate?")) return;
    try {
      await API.delete(`/admin/candidates/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      fetchCandidates();
    } catch (err) {
      alert(err.response?.data?.message || "Failed to delete candidate");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  return (
    <div className="container">
      <h1>Admin Dashboard</h1>
      <button onClick={() => navigate("/results")}>View Live Results</button>

      <div>
        <h2>Add Candidate</h2>
        <input
          type="text"
          placeholder="Candidate Name"
          value={newCandidate}
          onChange={(e) => setNewCandidate(e.target.value)}
        />
        <button onClick={handleAddCandidate}>Add Candidate</button>
      </div>

      <div>
        <h2>All Candidates</h2>
        <ul>
          {candidates.map((c) => (
            <li key={c._id}>
              {c.name} — {c.votes} votes
              <button onClick={() => handleDeleteCandidate(c._id)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>

      <button onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default AdminDashboard;
