import React, { useEffect, useState } from "react";
import axios from "axios";

function CandidateList() {
  const [candidates, setCandidates] = useState([]);
  const [selectedVotes, setSelectedVotes] = useState({});

  useEffect(() => {
    const fetchCandidates = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/candidates");
        setCandidates(res.data);
      } catch (error) {
        console.error("Error fetching candidates:", error);
      }
    };
    fetchCandidates();
  }, []);

  // group candidates by position
  const grouped = candidates.reduce((acc, c) => {
    acc[c.position] = acc[c.position] ? [...acc[c.position], c] : [c];
    return acc;
  }, {});

  const handleVote = (position, candidateId) => {
    setSelectedVotes((prev) => ({ ...prev, [position]: candidateId }));
  };

  const submitVotes = async () => {
    try {
      console.log("Votes submitted:", selectedVotes);
      alert("‚úÖ Your votes have been submitted successfully!");
      // (you can later send this data to a /api/votes endpoint)
    } catch (error) {
      console.error(error);
      alert("‚ùå Failed to submit votes");
    }
  };

  return (
    <div className="bg-white p-6 rounded-2xl shadow-md">
      <h2 className="text-2xl font-bold mb-6 text-blue-700 text-center">
        üó≥Ô∏è Vote for Your Candidates
      </h2>

      {Object.keys(grouped).map((position) => (
        <div key={position} className="mb-8">
          <h3 className="text-xl font-semibold mb-3 text-gray-800">
            {position}
          </h3>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
            {grouped[position].map((candidate) => (
              <div
                key={candidate._id}
                className={`p-4 border rounded-xl cursor-pointer transition ${
                  selectedVotes[position] === candidate._id
                    ? "border-blue-600 bg-blue-50"
                    : "border-gray-200"
                }`}
                onClick={() => handleVote(position, candidate._id)}
              >
                <img
                  src={
                    candidate.photo ||
                    "https://cdn-icons-png.flaticon.com/512/149/149071.png"
                  }
                  alt={candidate.name}
                  className="w-20 h-20 mx-auto rounded-full mb-2"
                />
                <h4 className="text-lg font-semibold text-center">
                  {candidate.name}
                </h4>
                <p className="text-sm text-center text-gray-600">
                  {candidate.department} ({candidate.yearOfStudy})
                </p>
              </div>
            ))}
          </div>
        </div>
      ))}

      {candidates.length > 0 && (
        <button
          onClick={submitVotes}
          className="w-full bg-blue-700 text-white p-3 rounded-lg hover:bg-blue-800 transition"
        >
          Submit My Votes
        </button>
      )}
    </div>
  );
}

export default CandidateList;
