import React, { useState } from "react";
import axios from "axios";

function CandidateRegister() {
  const [formData, setFormData] = useState({
    name: "",
    position: "",
    department: "",
    yearOfStudy: "",
    manifesto: "",
    photo: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/api/candidates/register", formData);
      alert("✅ Candidate registered successfully!");
      console.log(res.data);
    } catch (error) {
      console.error(error);
      alert("❌ Failed to register candidate");
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex justify-center items-center">
      <form
        onSubmit={handleSubmit}
        className="bg-white p-8 rounded-2xl shadow-lg w-full max-w-lg"
      >
        <h2 className="text-2xl font-bold mb-6 text-center text-blue-700">
          Candidate Registration Form
        </h2>

        <input
          type="text"
          name="name"
          placeholder="Full Name"
          onChange={handleChange}
          value={formData.name}
          className="w-full p-2 mb-3 border rounded"
          required
        />

        <input
          type="text"
          name="position"
          placeholder="Position (e.g., Guild President)"
          onChange={handleChange}
          value={formData.position}
          className="w-full p-2 mb-3 border rounded"
          required
        />

        <input
          type="text"
          name="department"
          placeholder="Department"
          onChange={handleChange}
          value={formData.department}
          className="w-full p-2 mb-3 border rounded"
          required
        />

        <input
          type="text"
          name="yearOfStudy"
          placeholder="Year of Study (e.g., Year 2)"
          onChange={handleChange}
          value={formData.yearOfStudy}
          className="w-full p-2 mb-3 border rounded"
          required
        />

        <textarea
          name="manifesto"
          placeholder="Manifesto / Vision Statement"
          onChange={handleChange}
          value={formData.manifesto}
          className="w-full p-2 mb-3 border rounded"
        />

        <input
          type="text"
          name="photo"
          placeholder="Photo URL (optional)"
          onChange={handleChange}
          value={formData.photo}
          className="w-full p-2 mb-3 border rounded"
        />

        <button
          type="submit"
          className="w-full bg-blue-700 text-white p-2 rounded hover:bg-blue-800 transition"
        >
          Register Candidate
        </button>
      </form>
    </div>
  );
}

export default CandidateRegister;
