import React, { useEffect, useState } from "react";
import axios from "axios";

function StudentDashboard() {
  const [candidates, setCandidates] = useState([]);
  const [votedPositions, setVotedPositions] = useState([]);
  const [loading, setLoading] = useState(true);

  // Get student info from localStorage (assuming login stores it)
  const student = JSON.parse(localStorage.getItem("user"));
  const studentId = student?._id;

  // Fetch all candidates
  useEffect(() => {
    const fetchCandidates = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/candidates");
        setCandidates(res.data);
      } catch (err) {
        console.error(err);
        alert("Failed to load candidates");
      } finally {
        setLoading(false);
      }
    };

    // Fetch already voted positions for this student
    const fetchVotes = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/votes/student/${studentId}`);
        const positions = res.data.map(v => v.position);
        setVotedPositions(positions);
      } catch (err) {
        console.error(err);
      }
    };

    fetchCandidates();
    if (studentId) fetchVotes();
  }, [studentId]);

  // Handle voting
  const handleVote = async (candidateId, position) => {
    if (votedPositions.includes(position)) {
      alert(`You already voted for ${position}`);
      return;
    }

    try {
      const res = await axios.post("http://localhost:5000/api/votes/cast", {
        studentId,
        candidateId,
        position,
      });

      alert(res.data.message);
      setVotedPositions([...votedPositions, position]); // update locally
    } catch (err) {
      alert(err.response?.data?.message || "Error casting vote");
    }
  };

  if (loading) return <p>Loading candidates...</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>Candidate List</h2>
      {candidates.length === 0 && <p>No candidates available</p>}
      <ul>
        {candidates.map((candidate) => (
          <li key={candidate._id} style={{ marginBottom: "10px" }}>
            <strong>{candidate.name}</strong> - {candidate.position} ({candidate.department})
            {candidate.yearOfStudy && <> - Year: {candidate.yearOfStudy}</>}
            <p>{candidate.manifesto}</p>
            {candidate.photo && (
              <img
                src={candidate.photo}
                alt={candidate.name}
                style={{ width: "100px", borderRadius: "8px" }}
              />
            )}
            <br />
            <button
              onClick={() => handleVote(candidate._id, candidate.position)}
              disabled={votedPositions.includes(candidate.position)}
              style={{
                marginTop: "5px",
                padding: "5px 10px",
                cursor: votedPositions.includes(candidate.position) ? "not-allowed" : "pointer",
              }}
            >
              {votedPositions.includes(candidate.position) ? "Already Voted" : "Vote"}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default StudentDashboard;
