import React, { useState, useEffect } from "react";
import API from "../api/api";
import { useNavigate } from "react-router-dom";

const AdminDashboard = () => {
  const [candidates, setCandidates] = useState([]);
  const [newCandidate, setNewCandidate] = useState("");
  const navigate = useNavigate();

  const fetchCandidates = async () => {
    try {
      const res = await API.get("/admin/candidates");
      setCandidates(res.data);
    } catch (err) {
      alert(err.response?.data?.message || "Failed to fetch candidates");
    }
  };

  useEffect(() => {
    fetchCandidates();
  }, []);

  const handleAddCandidate = async () => {
    if (!newCandidate) return alert("Enter candidate name");
    try {
      await API.post("/admin/candidates", { name: newCandidate });
      setNewCandidate("");
      fetchCandidates();
    } catch (err) {
      alert(err.response?.data?.message || "Failed to add candidate");
    }
  };

  const handleDeleteCandidate = async (id) => {
    if (!window.confirm("Are you sure you want to delete this candidate?")) return;
    try {
      await API.delete(`/admin/candidates/${id}`);
      fetchCandidates();
    } catch (err) {
      alert(err.response?.data?.message || "Failed to delete candidate");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  return (
    <div className="container">
      <h1>Admin Dashboard</h1>
      <button onClick={() => navigate("/results")}>View Live Results</button>


      <div>
        <h2>Add Candidate</h2>
        <input
          type="text"
          placeholder="Candidate Name"
          value={newCandidate}
          onChange={e => setNewCandidate(e.target.value)}
        />
        <button onClick={handleAddCandidate}>Add Candidate</button>
      </div>

      <div>
        <h2>All Candidates</h2>
        <ul>
          {candidates.map(c => (
            <li key={c._id}>
              {c.name} â€” {c.votes} votes
              <button onClick={() => handleDeleteCandidate(c._id)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>

      <button onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default AdminDashboard;
